// SPDX-License-IDentifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract SupplyChain {
  address public owner = msg.sender;
  uint32 public g_participant_id = 0;
  uint256 NULL_NUMBER = 0;

  // participants
  struct participant {
    uint32 participantID;
    string userName;
    string participantType;
    address participantAddress;
  }

  mapping(uint32 => participant) public participants;

  constructor() public {}

  // ===== participant =====
  function addParticipant(
    string memory _name,
    string memory _pType,
    address _pAddress
  ) public returns (uint256) {
    {
      g_participant_id++;
      participants[g_participant_id].participantID = g_participant_id;
      participants[g_participant_id].userName = _name;
      participants[g_participant_id].participantAddress = _pAddress;
      participants[g_participant_id].participantType = _pType;

      return g_participant_id;
    }
  }

  function getParticipant(uint32 _participant_id)
    public
    view
    returns (
      string memory,
      address,
      string memory
    )
  {
    return (
      participants[_participant_id].userName,
      participants[_participant_id].participantAddress,
      participants[_participant_id].participantType
    );
  }
}
